import img from "../../../assets/img/backgroundSignin.jpg"
import classes from "./AddHousePage.module.css"
import { Link } from "react-router-dom";
import { useState, useEffect, useRef, useContext } from "react"
import AuthContext from '../../../store/auth-context';
import {getProvince, getDistrict, getWard} from "../../../api/ProvinceApi"

const typeData = [
    {
        text: "Family House",
        value: "0"
    },
    {
        text: "House & Villa",
        value: "1"
    },
    {
        text: "Apartment",
        value: "2"
    },
    {
        text: "Office & Studio",
        value: "3"
    },
    {
        text: "Villa & Condo",
        value: "4"
    },
    
]

const AddHousePage = () => {
    const [cityData, setCityData] = useState([])
    const [districtData, setDistrictData] = useState([])
    const [wardData, setWardData] = useState([])
    
    const authCtx = useContext(AuthContext)
    
    const categoryInputRef = useRef("")
    const cityInputRef = useRef("")
    const districtInputRef = useRef("")
    const wardInputRef = useRef("")
    const detailInputRef = useRef("")
    const descriptionInputRef = useRef("")
    
    useEffect(() => {
        getProvince().then(data => {
            const tempCity = []
            data.forEach(data => {
                tempCity.push({
                    text: data.name,
                    value: data.code,
                })
            })
            setCityData(tempCity)
        })
        .catch(error => console.log('Error', error))
    }, [])
    
    useEffect(() => {
        getDistrict(city).then(data => {
            const tempDistrict = []
            data.districts.forEach(data => {
                tempDistrict.push({
                    text: data.name,
                    value: data.code,
                })
            })
            setDistrictData(tempDistrict)
        })
        .catch(error => console.log('Error', error))
    }, [city])
    
    useEffect(() => {
        getWard(district).then(data => {
            const tempWard = []
            data.wards.forEach(data => {
                tempWard.push({
                    text: data.name,
                    value: data.code,
                })
            })
            setWardData(tempWard)
        })
        .catch(error => console.log('Error', error))
    }, [district])
    
    const submitHandler = (event) => {
        event.preventDefault();
        
        const enteredCategory = categoryInputRef.current.value;
        const enteredCity = cityInputRef.current.value;
        const enteredDistrict = districtInputRef.current.value;
        const enteredWard = wardInputRef.current.value;
        const enteredDetail = detailInputRef.current.value;
        const enteredDescription = descriptionInputRef.current.value;
        
        console.log(enteredCategory, enteredCity, enteredDistrict, enteredWard, enteredDetail, enteredDescription);
        
        // optional: Add validation
        
        fetch(
            'http://localhost:8080/api/balanha/addhouse',
            {
                method: 'POST',
                body: JSON.stringify({
                    category: enteredCategory,
                    city: enteredCity,
                    district: enteredDistrict,
                    ward: enteredWard,
                    detail: enteredDetail,
                    description: enteredDescription
                }),
                headers: {
                    'Content-Type': 'application/json',
                    'user_id': `${authCtx.userId}`, 
                    'token': `${authCtx.token}`
                },
            }
        ).then((res) => {
            if (res.ok) {
                return res.json();
            } else {
                return res.json().then((data) => {
                    let errorMessage = 'Authentication failed!';
                    // if (data && data.error && data.error.message) {
                    //   errorMessage = data.error.message;
                    // }
        
                    throw new Error(errorMessage);
                });
            }
        })
        .then((data) => {
            console.log(data)
            alert("Success")
        })
        .catch((err) => {
            alert(err.message);
        });
    }
    
    return (
        <section className={classes.back}>
            <div className={classes.center}>
                <h1>Add House</h1>
                <form method="POST" onSubmit={submitHandler}>
                    <div className={classes.txt_field}>
                        <select 
                            ref={categoryInputRef}
                        >
                            {typeData.map(type => <option value={type.value}>{type.text}</option>)}
                        </select>
                        <span></span>
                        <label>Type</label>
                    </div>
                    <div className={classes.txt_field}>
                        <select 
                            ref={cityInputRef}
                        >
                            {typeData.map(type => <option value={type.value}>{type.text}</option>)}
                        </select>
                        <span></span>
                        <label>City</label>
                    </div>
                    <div className={classes.txt_field}>
                        <select 
                            ref={districtInputRef}
                        >
                            {typeData.map(type => <option value={type.value}>{type.text}</option>)}
                        </select>
                        <span></span>
                        <label>District</label>
                    </div>
                    <div className={classes.txt_field}>
                        <select 
                            ref={wardInputRef}
                        >
                            {typeData.map(type => <option value={type.value}>{type.text}</option>)}
                        </select>
                        <span></span>
                        <label>Ward</label>
                    </div>
                    <div className={classes.txt_field}>
                        <input type="text" required ref={detailInputRef}/>
                        <span></span>
                        <label>Detail</label>
                    </div>
                    <div className={classes.txt_field}>
                        <input type="text" required ref={descriptionInputRef}/>
                        <span></span>
                        <label>Description</label>
                    </div>
                    <button type="submit">Add</button>
                    <div className={classes.signup_link}>
                        <Link to="/">home</Link>
                    </div>
                </form>
            </div>
            <img src={img} alt='' />
        </section>
    )
}

export default AddHousePage
