import { useState, useContext, Fragment } from "react";
import logo from "../../../../assets/img/logo.png"
import classes from "./Header.module.css"
import { Link, useHistory } from "react-router-dom";
import AuthContext from '../../../../store/auth-context';
import {logoutApi} from '../../../../api/AuthApi'

const Data = [
    {
        text: "home",
        path: "/",
    },
    {
        text: "about",
        path: "/about",
    },
    {
        text: "type",
        path: "/type",
    },
    {
        text: "post",
        path: "/post",
    },
    {
        text: "contact",
        path: "/contact",
    },
]

const Header = () => {
    const history = useHistory()
    
    const authCtx = useContext(AuthContext)
    
    const [navList, setNavList] = useState(false)
    
    const logoutHandler = () => {
        // fetch(
        //     'http://localhost:8080/api/balanha/logout',
        //     {
        //         method: 'POST',
        //         headers: {
        //             'Content-Type': 'application/json',
        //             'user_id': `${authCtx.userId}`, 
        //             'token': `${authCtx.token}`
        //         },
        //     }
        // ).then((res) => {
        //     if (res.ok) {
        //         return res.json();
        //     } else {
        //         return res.json().then((data) => {
        //             let errorMessage = 'Authentication failed!';
        //             // if (data && data.error && data.error.message) {
        //             //   errorMessage = data.error.message;
        //             // }
        
        //             throw new Error(errorMessage);
        //         });
        //     }
        // })
        logoutApi(`${authCtx.userId}`)
        .then((data) => {
            console.log(data);
            authCtx.logout();
            history.replace("/signin")
        })
        .catch((err) => {
            alert(err.message);
        });
    }
    
    return (
        <header>
            <div className={`${classes.container} ${classes.flex}`}>
                <div> 
                    <img src={logo} alt='logo' />
                </div>
                <div className={classes.nav}>
                    <ul className={navList ? classes.small : classes.flex}>
                        {Data.map((list, index) => (
                            <li key={index}>
                                <Link to={list.path}>{list.text}</Link>
                            </li>
                        ))}
                    </ul>
                </div>
                <div className={`${classes.button} ${classes.flex} ${classes.account}`}>
                    {authCtx.username && 
                        <Fragment>
                            <Link className={classes.signin}>
                                <i className='fa fa-sign-out'></i> {authCtx.username}
                            </Link>
                            
                            <ul className={classes.navbar_account}>
                                <li><Link to={"/profile"}>My Account</Link></li>
                                <li><Link to={"/manage"}>My List</Link></li>
                                <li><Link to={"/rent"}>My Rent</Link></li>
                                <li><Link onClick={logoutHandler}>Logout</Link></li>
                            </ul>
                        </Fragment>
                    }
                    
                    {!authCtx.username && 
                        <Link to="/signin" className={classes.signin}>
                            <i className='fa fa-sign-out'></i> Sign In
                        </Link>
                    }
                </div>

                <div className={classes.toggle}>
                    <button onClick={() => setNavList(!navList)}>
                        {navList ? <i className='fa fa-times'></i> : <i className='fa fa-bars'></i>}
                    </button>
                </div>
            </div>
        </header>
    )
}

export default Header;
