import { useState, useContext } from "react";
import logo from "../../../../assets/img/logo.png"
import classes from "./Header.module.css"
import { Link } from "react-router-dom";
import AuthContext from '../../../../store/auth-context';

const Data = [
    {
        text: "home",
        path: "/",
    },
    {
        text: "about",
        path: "/about",
    },
    {
        text: "type",
        path: "/type",
    },
    {
        text: "post",
        path: "/post",
    },
    {
        text: "manage",
        path: "/manage",
    },
    {
        text: "contact",
        path: "/contact",
    },
]

const Header = () => {
    const authCtx = useContext(AuthContext)
    
    const [navList, setNavList] = useState(false)
    const [navbarAccountClasses, setNavbarAccountClasses] = useState(`${classes.navbar_account}`)
    
    const accountClickHandler = () => {
        if(navbarAccountClasses.includes('navbar_account_active')){
            setNavbarAccountClasses(`${classes.navbar_account}`)
        }else{
            setNavbarAccountClasses(`${classes.navbar_account} ${classes.navbar_account_active}`)
        }
    }
    
    return (
        <header>
            <div className={`${classes.container} ${classes.flex}`}>
                <div> 
                    <img src={logo} alt='logo' />
                </div>
                <div className={classes.nav}>
                    <ul className={navList ? classes.small : classes.flex}>
                        {Data.map((list, index) => (
                            <li key={index}>
                                <Link to={list.path}>{list.text}</Link>
                            </li>
                        ))}
                    </ul>
                </div>
                <div className={`${classes.button} ${classes.flex} ${classes.account}`} onClick={accountClickHandler}>
                    {authCtx.username && 
                        <Link className={classes.signin}>
                            <i className='fa fa-sign-out'></i> {authCtx.username}
                        </Link>
                    }
                    
                    {!authCtx.username && 
                        <Link to="/signin" className={classes.signin}>
                            <i className='fa fa-sign-out'></i> Sign In
                        </Link>
                    }
                </div>
                
                <ul className={navbarAccountClasses}>
                    <li><Link to={"/profile"}>My Account</Link></li>
                    <li><Link to={"/manage"}>My List</Link></li>
                    <li><Link to={"/rent"}>My Rent</Link></li>
                    <li><Link to={"/#"}>Sign Out</Link></li>
                </ul>

                <div className={classes.toggle}>
                    <button onClick={() => setNavList(!navList)}>
                        {navList ? <i className='fa fa-times'></i> : <i className='fa fa-bars'></i>}
                    </button>
                </div>
            </div>
        </header>
    )
}

export default Header;
