import Back from "../../common/Back/Back"
import img from "../../../../assets/img/backgroundPost.jpg"
import classes from "./ManageRoom.module.css"
import RoomCard from "./RoomCard"
import { useParams } from 'react-router-dom';
import { useState, useEffect, useContext } from "react"
import AuthContext from '../../../../store/auth-context';
import {getOwnedRoomApi} from "../../../../api/RoomApi"


const Data = [
    {
      id: 1,
      cover: "https://funny-daffodil-350bc9.netlify.app/images/list/p-1.png",
      name: "Red Carpet Real Estate",
      location: "210 Zirak Road, Canada",
      category: "For Rent",
      price: "$3,700",
      type: "Apartment",
    },
    {
      id: 2,
      cover: "https://funny-daffodil-350bc9.netlify.app/images/list/p-2.png",
      name: "Fairmount Properties",
      location: "5698 Zirak Road, NewYork",
      category: "For Sale",
      price: "$9,750",
      type: "Condos",
    },
    {
      id: 3,
      cover: "https://funny-daffodil-350bc9.netlify.app/images/list/p-7.png",
      name: "The Real Estate Corner",
      location: "5624 Mooker Market, USA",
      category: "For Rent",
      price: "$5,860",
      type: "Offices",
    },
    {
      id: 4,
      cover: "https://funny-daffodil-350bc9.netlify.app/images/list/p-4.png",
      name: "Herringbone Realty",
      location: "5621 Liverpool, London",
      category: "For Sale",
      price: "$7,540",
      type: "Homes & Villas",
    },
    {
      id: 5,
      cover: "https://funny-daffodil-350bc9.netlify.app/images/list/p-5.png",
      name: "Brick Lane Realty",
      location: "210 Montreal Road, Canada",
      category: "For Rent",
      price: "$4,850",
      type: "Commercial",
    },
    {
      id: 6,
      cover: "https://funny-daffodil-350bc9.netlify.app/images/list/p-6.png",
      name: "Banyon Tree Realty",
      location: "210 Zirak Road, Canada",
      category: "For Sale",
      price: "$2,742",
      type: "Apartment",
    },
]

const ManagerRoom = () => {
    const params = useParams()

    const id = params.id
    
    const authCtx = useContext(AuthContext)
    
    const [rooms, setRooms] = useState([])
    
    useEffect(() => {
        getOwnedRoomApi(id, authCtx.userId, authCtx.token)
        .then((data) => {
            const tempData = []
            data.data.forEach(item => {
                tempData.push({
                    cover: `https://funny-daffodil-350bc9.netlify.app/images/list/p-1.png`,
                    totalPrice: item.rent_price + item.water_price + item.electricity_price + item.service_price,
                    ...item
                })
            })
            setRooms(tempData)
            console.log(rooms)
        })
        .catch((err) => {
                alert(err.message);
        });
    }, [])
  
    return (
        <section>
            <Back name='Room' title='Room Grid - Our Rooms' cover={img}/>
            <section className={`${classes.recent} ${classes.padding}`}>
                <div className={classes.container}>
                    <div className={`${classes.grid3} ${classes.mtop}`}>
                        {rooms.map((item, index) => <RoomCard item={item} index={index}/>)}
                    </div>
                </div>
            </section>
        </section>
    )
}

export default ManagerRoom;
