import classes from "./SignupPage.module.css"
import img from "../../../assets/img/backgroundSignin.jpg"
import { useRef } from "react"

const SignupPage = () => {
    const usernameInputRef = useRef("")
    const passwordInputRef = useRef("")
    const passwordRepeatInputRef = useRef("")
    
    const submitHandler = (event) => {
        event.preventDefault();
        
        const enteredUsername = usernameInputRef.current.value;
        const enteredPassword = passwordInputRef.current.value;
        const enteredPasswordRepeat = passwordRepeatInputRef.current.value;
        
        // optional: Add validation
        
        if(enteredPassword !== enteredPasswordRepeat) {
            console.log("pass deo giong nhau")
            return;
        }
        
        
        fetch(
            'http://localhost:8080//api/balanha/register',
            {
                method: 'POST',
                body: JSON.stringify({
                    username: enteredUsername,
                    password: enteredPassword,
                }),
                headers: {
                    'Content-Type': 'application/json',
                },
            }
        ).then((res) => {
            if (res.ok) {
                return res.json().then((data) => {
                    // show an error modal
                    console.log(data);
                  });
            } else {
                return res.json().then((data) => {
                    // show an error modal
                    console.log(data);
                });
            }
        });
    }
    
    return (
        <section className={classes.back}>
            <div class={classes.center}>
                <h1>Sign Up</h1>
                <form method={classes.post} onSubmit={submitHandler}>
                    <div class={classes.txt_field}>
                        <input type="text" required ref={usernameInputRef}/>
                        <span></span>
                        <label>Username</label>
                    </div>
                    <div class={classes.txt_field}>
                        <input type="password" required ref={passwordInputRef}/>
                        <span></span>
                        <label>Password</label>
                    </div>
                    <div class={classes.txt_field}>
                        <input type="password" required ref={passwordRepeatInputRef}/>
                        <span></span>
                        <label>Password repeat</label>
                    </div>
                    <button type="submit">Sign Up</button>
                    <div class={classes.signin_link}>
                       Have a account? <a href="#">Sign in</a>
                    </div>
                </form>
            </div>
            <img src={img} alt='' />
        </section>
    )
}

export default SignupPage
