import classes from "./LandlordRegisterPage.module.css"
import img from "../../../assets/img/backgroundSignin.jpg"
import { useRef, useContext } from "react"
import AuthContext from '../../../store/auth-context';
import { Link, useHistory } from "react-router-dom";
import { landlordRegisterApi } from "../../../api/AuthApi";

const LandlordRegisterPage = () => {
    const citizenIdInputRef = useRef('')
    const bankInputRef = useRef('')
    const bankNumberInputRef = useRef('')
    
    const authCtx = useContext(AuthContext)
    
    const submitHandler = (event) => {
        event.preventDefault();
        
        const enteredCitizenId = citizenIdInputRef.current.value;
        const enteredBank = bankInputRef.current.value;
        const enteredBankNumber = bankNumberInputRef.current.value;
        
        // optional: Add validation
        landlordRegisterApi(authCtx.userId, authCtx.token, enteredCitizenId, enteredBank, enteredBankNumber)
        .then((data) => {
            authCtx.login(data.data.token, data.data.id, data.data.username, data.data.role)
            history.replace("/")
        })
        .catch((err) => {
            alert(err.message);
        });
        
    }
    
    return (
        <section className={classes.back}>
            <div class={classes.center}>
                <h1>Become a Landlord</h1>
                <form method="POST" onSubmit={submitHandler}>
                    <div class={classes.txt_field}>
                        <input type="text" required ref={citizenIdInputRef}/>
                        <span></span>
                        <label>citizen Identification</label>
                    </div>
                    <div class={classes.txt_field}>
                        <input type="text" required ref={bankInputRef}/>
                        <span></span>
                        <label>Bank</label>
                    </div>
                    <div class={classes.txt_field}>
                        <input type="text" required ref={bankNumberInputRef}/>
                        <span></span>
                        <label>Bank Number</label>
                    </div>
                    <button type="submit">Register</button>
                    <div class={classes.home_link}>
                        <Link to="/">home</Link>
                    </div>
                </form>
            </div>
            <img src={img} alt='' />
        </section>
    )
}

export default LandlordRegisterPage
