import Heading from "../../common/Heading/Heading"
import RecentCard from "./RecentCard"
import classes from "./Recent.module.css"
import {useState, useEffect} from "react"
import {getAllRoomAvailable} from '../../../../api/RoomApi'
import { Api } from "../../../../configs/App"

const Recent = (props) => {
    const [data, setData] = useState([])
    
    useEffect(() => {
        getAllRoomAvailable()
        .then((data) => {
            const temp = []
            data.forEach(data => {
                temp.push({
                    room_id: data.id,
                    room_name: data.name,
                    cover: `${Api}${data.image}`,
                    category: data.category.name,
                    city: data.house.city,
                    district: data.house.district,
                    ward: data.house.ward,
                    totalPrice: data.totalPrice
                })
            })
            setData(temp)
        })
        .catch((err) => {
            alert(err.message);
        });
    }, [props.filter])
  
    return (
        <section className={`${classes.recent} ${classes.padding}`}>
            <div className={classes.container}>
                <Heading title='Recent Property Listed' subtitle='Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim veniam.' />
                <div className={`${classes.grid3} ${classes.mtop}`}>
                    {data.map((item, index) => <RecentCard item={item} index={index}/>)}
                </div>
            </div>
        </section>
    )
}

export default Recent
