import classes from "./Filter.module.css"
import Heading from "../../common/Heading/Heading"
import {useState, useEffect} from "react"
import {getProvince, getDistrict, getWard} from "../../../../api/ProvinceApi"

const typeData = [
    {
        text: "Family House",
        value: "Family House"
    },
    {
        text: "House & Villa",
        value: "House & Villa"
    },
    {
        text: "Apartment",
        value: "Apartment"
    },
    {
        text: "Office & Studio",
        value: "Office & Studio"
    },
    {
        text: "Villa & Condo",
        value: "Villa & Condo"
    },
    
]

const Filter = () => {
    const [cityData, setCityData] = useState([])
    const [districtData, setDistrictData] = useState([])
    const [wardData, setWardData] = useState([])
    
    useEffect(() => {
        getProvince().then(data => {
            console.log(typeof data)
        })
        .catch(error => console.log('Error', error))
    }
    , )
    
    
    const [values, setValues] = useState({
        city: '',
        district: '',
        ward: '',
        type: '',
        priceMin: 0,
        priceMax: 0
    })
    
    const {city, district, ward, type, priceMin, priceMax} = values
    
    const handleChange = (key) => {
        return (event) => {
            setValues({...values, [key]: event.target.value})
        }
    }
    
    const submitHandler = (event) => {
        event.preventDefault()

        setValues({
            ...values,
        })

        console.log(values)
    }
    
    return (
        <section className={classes.filter}>
            <div className={classes.container}>
            <Heading title='Search Your Next Home ' subtitle='Find new & featured property located in your local city.' />

            <form className={classes.flex}>
                <div className={classes.box}>
                    <span>City</span>
                    <select 
                        id="city" 
                        name="city" 
                        value={city}
                        onChange={handleChange('city')}
                    >
                        {cityData.map(city => <option value={city.value}>{city.text}</option>)}
                    </select>
                </div>
                <div className={classes.box}>
                    <span>District</span>
                    <select 
                        id="district" 
                        name="district"
                        value={district}
                        onChange={handleChange('district')}
                    >
                        {districtData.map(district => <option value={district.value}>{district.text}</option>)}
                    </select>
                </div>
                <div className={classes.box}>
                    <span>Ward</span>
                    <select 
                        id="ward" 
                        name="ward"
                        value={ward}
                        onChange={handleChange('ward')}
                    >
                        {districtData.map(ward => <option value={ward.value}>{ward.text}</option>)}
                    </select>
                </div>
                <div className={classes.box}>
                    <span>Property Type</span>
                    <select 
                        id="type" 
                        name="type"
                        value={type}
                        onChange={handleChange('type')}
                    >
                        {typeData.map(type => <option value={type.value}>{type.text}</option>)}
                    </select>
                </div>
                <div className={classes.box}>
                    <span>Price Max</span>
                    <input 
                        type='number' 
                        placeholder='Price Max'
                        value={priceMax}
                        onChange={handleChange('priceMax')}
                    />
                </div>
                <div className={classes.box}>
                    <span>Price Min</span>
                    <input 
                        type='number' 
                        placeholder='Price Min'
                        value={priceMin}
                        onChange={handleChange('priceMin')}
                    />
                </div>
                <div className={classes.box}>
                    <h4>Advance Filter</h4>
                </div>
                <button type="submit" onClick={submitHandler}>
                    <i className='fa fa-search'></i>
                </button>
            </form>
            </div>
        </section>
    )
}

export default Filter;
