import Apis, {endpoints} from '../configs/App'

export const getAllRoomAvailable = (filter) => {
    return Apis.get(endpoints['room'], {
        params: filter
    })
    .then((response) => {return response.data})
    .catch((error) => console.error(error))
}

export const getRoomByRoomId = (roomId) => {
    return Apis.get(endpoints['room-detail'](roomId),)
    .then((response) => {return response.data})
    .catch((error) => console.error(error))
}

export const getRoomListOfHouse = (houseId) => {
    return Apis.get(endpoints['get-room-list-of-house'](houseId),)
    .then((response) => {return response})
    .catch((error) => console.error(error))
}

export const createRoomApi = (room, userId, token) => {
    return fetch(
        'http://localhost:8080/api/balanha/addroom',
        {
            method: 'POST',
            body: JSON.stringify({
                house_id: room.house_id,
                room_name: room.room_name,
                room_type: room.room_type,
                area: room.area,
                max_tenant: room.max_tenant,
                rent_price: room.rent_price,
                water_price: room.water_price,
                electricity_price: room.electricity_price,
                service_price: room.service_price,
                detail: room.detail,
            }),
            headers: {
                'Content-Type': 'application/json',
                'user_id': userId,
                'token': token
            },
        }
    ).then((res) => {

        if (res.ok) {
            return res.json();
        } else {
            return res.json().then((data) => {
                let errorMessage = 'Authentication failed!';
                // if (data && data.error && data.error.message) {
                //   errorMessage = data.error.message;
                // }

                throw new Error(errorMessage);
            });
        }
    })
} 

export const getOwnedRoomApi = (house_id, userId, token) => {
    return fetch(
        `http://localhost:8080/api/balanha/getownedroom`,
        {
            method: 'POST',
            body: JSON.stringify({
                house_id: house_id,
            }),
            headers: {
                'Content-Type': 'application/json',
                'user_id': userId, 
                'token': token
            },
        }
    ).then((res) => {
        if (res.ok) {
            return res.json();
        } else {
            return res.json().then((data) => {
                let errorMessage = 'Authentication failed!';
                // if (data && data.error && data.error.message) {
                //   errorMessage = data.error.message;
                // }
    
                throw new Error(errorMessage);
            });
        }
    })
}
