export const getRoomListApi = () => {
    return fetch(`http://localhost:8080/api/balanha/getroomlist`, {
        method: 'POST',
        body: JSON.stringify({
            // category: , 
            // city: , 
            // district: , 
            // minPrice: , 
            // maxPrice: , 
            // minArea: , 
            // maxArea: , 
        }),
        headers: {
            'Content-Type': 'application/json',
        },
    })
        .then((response) => {
            return response.json()
        })
        .catch((error) => console.error(error))
}

export const createRoomApi = (room, userId, token) => {
    return fetch(
        'http://localhost:8080/api/balanha/addroom',
        {
            method: 'POST',
            body: JSON.stringify({
                house_id: room.house_id,
                room_name: room.room_name,
                room_type: room.room_type,
                area: room.area,
                max_tenant: room.max_tenant,
                rent_price: room.rent_price,
                water_price: room.water_price,
                electricity_price: room.electricity_price,
                service_price: room.service_price,
                detail: room.detail,
            }),
            headers: {
                'Content-Type': 'application/json',
                'user_id': userId,
                'token': token
            },
        }
    ).then((res) => {

        if (res.ok) {
            return res.json();
        } else {
            return res.json().then((data) => {
                let errorMessage = 'Authentication failed!';
                // if (data && data.error && data.error.message) {
                //   errorMessage = data.error.message;
                // }

                throw new Error(errorMessage);
            });
        }
    })
}
