const AUTH_API = `http://localhost:8080/api/balanha/`

export const signinApi = (username, password) => {
    return fetch(
        `${AUTH_API}login`,
        {
            method: 'POST',
            body: JSON.stringify({
                username: username,
                password: password,
            }),
            headers: {
                'Content-Type': 'application/json',
            },
        }
    ).then((res) => {
        console.log(res)
        if (res.ok) {
            return res.json();
        } else {
            return res.json().then((data) => {
                let errorMessage = 'Authentication failed!';
                // if (data && data.error && data.error.message) {
                //   errorMessage = data.error.message;
                // }
    
                throw new Error(errorMessage);
            });
        }
    })
    .catch((error) => console.error(error))
}

export const signupApi = (username, password) => {
    fetch(
        `${AUTH_API}register`,
        {
            method: 'POST',
            body: JSON.stringify({
                username: username,
                password: password,
            }),
            headers: {
                'Content-Type': 'application/json',
            },
        }
    ).then((res) => {
        if (res.ok) {
            return res.json();
        } else {
            return res.json();
        }
    })
}

export const logoutApi = (userId, token) => {
    console.log(userId, token);
    fetch(
        `${AUTH_API}logout`,
        {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
                'user_id': userId, 
                'token': token
            },
        }
    ).then((res) => {
        if (res.ok) {
            return res.json();
        } else {
            return res.json().then((data) => {
                let errorMessage = 'Authentication failed!';
                // if (data && data.error && data.error.message) {
                //   errorMessage = data.error.message;
                // }
    
                throw new Error(errorMessage);
            });
        }
    })
}
