import Back from "../../common/Back/Back"
import img from "../../../../assets/img/backgroundPost.jpg"
import classes from "./ManageRoom.module.css"
import RoomCard from "./RoomCard"
import { useParams } from 'react-router-dom';
import { useState, useEffect, useContext } from "react"
import AuthContext from '../../../../store/auth-context';
import {getOwnedRoomApi} from "../../../../api/RoomApi"

const ManagerRoom = () => {
    const params = useParams()

    const id = params.id
    
    const authCtx = useContext(AuthContext)
    
    const [rooms, setRooms] = useState([])
    
    useEffect(() => {
        getOwnedRoomApi(id, authCtx.userId, authCtx.token)
        .then((data) => {
            const tempData = []
            data.data.forEach(item => {
                tempData.push({
                    cover: `https://funny-daffodil-350bc9.netlify.app/images/list/p-1.png`,
                    totalPrice: item.rent_price + item.water_price + item.electricity_price + item.service_price,
                    category: item.room_type,
                    ...item
                })
            })
            setRooms(tempData)
            console.log(rooms)
        })
        .catch((err) => {
                alert(err.message);
        });
    }, [])
  
    return (
        <section>
            <Back name='Room' title='Room Grid - Our Rooms' cover={img}/>
            <section className={`${classes.recent} ${classes.padding}`}>
                <div className={classes.container}>
                    <div className={`${classes.grid3} ${classes.mtop}`}>
                        {rooms.map((item, index) => <RoomCard item={item} index={index}/>)}
                    </div>
                </div>
            </section>
        </section>
    )
}

export default ManagerRoom;
