import classes from "./SigninPage.module.css"
import img from "../../../assets/img/backgroundSignin.jpg"
import { useRef, useContext } from "react"
import AuthContext from '../../../store/auth-context';

const SigninPage = () => {
    const authCtx = useContext(AuthContext);
    
    const usernameInputRef = useRef("")
    const passwordInputRef = useRef("")
    
    const submitHandler = (event) => {
        event.preventDefault();
        
        const enteredUsername = usernameInputRef.current.value;
        const enteredPassword = passwordInputRef.current.value;
        
        // optional: Add validation
        
        fetch(
            'http://localhost:8080/api/balanha/login',
            {
                method: 'POST',
                body: JSON.stringify({
                    username: enteredUsername,
                    password: enteredPassword,
                }),
                headers: {
                    'Content-Type': 'application/json',
                },
            }
        ).then((res) => {
            setIsLoading(false);
            if (res.ok) {
                return res.json();
            } else {
                return res.json().then((data) => {
                    let errorMessage = 'Authentication failed!';
                    // if (data && data.error && data.error.message) {
                    //   errorMessage = data.error.message;
                    // }
        
                    throw new Error(errorMessage);
                });
            }
        })
            .then((data) => {
            authCtx.login(data.idToken);
        })
            .catch((err) => {
            alert(err.message);
        });
    }
    
    return (
        <section className={classes.back}>
            <div className={classes.center}>
                <h1>Sign In</h1>
                <form method="POST" onSubmit={submitHandler}>
                    <div className={classes.txt_field}>
                        <input type="text" required ref={usernameInputRef}/>
                        <span></span>
                        <label>Username</label>
                    </div>
                    <div className={classes.txt_field}>
                        <input type="password" required ref={passwordInputRef}/>
                        <span></span>
                        <label>Password</label>
                    </div>
                    <div className={classes.pass}>Forgot Password?</div>
                    <button type="submit">Login</button>
                    <div className={classes.signup_link}>
                        Not a member? <a href="#">Signup</a>
                    </div>
                </form>
            </div>
            <img src={img} alt='' />
        </section>
    )
}

export default SigninPage
