import classes from "./Filter.module.css"
import Heading from "../../common/Heading/Heading"
import {useState, useRef} from "react"

const typeData = [
    {
        text: "Family House",
        value: "Family House"
    },
    {
        text: "House & Villa",
        value: "House & Villa"
    },
    {
        text: "Apartment",
        value: "Apartment"
    },
    {
        text: "Office & Studio",
        value: "Office & Studio"
    },
    {
        text: "Villa & Condo",
        value: "Villa & Condo"
    },
    
]

const cityData = [
    {
        text: "Ha Noi",
        value: "Ha Noi"
    },
    {
        text: "Ha Noi1",
        value: "Ha Noi1"
    },
    {
        text: "Ha Noi2",
        value: "Ha Noi2"
    },
    {
        text: "Ha Noi3",
        value: "Ha Noi3"
    },
    {
        text: "Ha Noi4",
        value: "Ha Noi4"
    }
]

const districtData = [
    {
        text: "Cau Giay",
        value: "Cau Giay"
    },
    {
        text: "Cau Giay1",
        value: "Cau Giay1"
    },
    {
        text: "Cau Giay2",
        value: "Cau Giay2"
    },
    {
        text: "Cau Giay3",
        value: "Cau Giay3"
    },
    {
        text: "Cau Giay4",
        value: "Cau Giay4"
    },
]

const Filter = () => {
    const [city, setCity] = useState("")
    const [district, setDistrict] = useState("")
    const [type, setType] = useState("")
    const [priceMin, setPriceMin] = useState(0)
    const [priceMax, setPriceMax] = useState(0)
    
    const cityRef = useRef("")
    const districtRef = useRef("")
    const typeRef = useRef("")
    const priceMinRef = useRef(0)
    const priceMaxRef = useRef(0)
    
    return (
        <section className={classes.filter}>
            <div className={classes.container}>
            <Heading title='Search Your Next Home ' subtitle='Find new & featured property located in your local city.' />

            <form className={classes.flex}>
                <div className={classes.box}>
                    <span>City</span>
                    <select id="city" name="city" value={cityRef}>
                        {cityData.map(city => <option value={city.value}>{city.text}</option>)}
                    </select>
                </div>
                <div className={classes.box}>
                    <span>District</span>
                    <select id="district" name="district">
                        {districtData.map(district => <option value={district.value}>{district.text}</option>)}
                    </select>
                </div>
                <div className={classes.box}>
                    <span>Property Type</span>
                    <select id="type" name="type">
                        {typeData.map(type => <option value={type.value}>{type.text}</option>)}
                    </select>
                </div>
                <div className={classes.box}>
                    <span>Price Max</span>
                    <input type='number' placeholder='Price Max' />
                </div>
                <div className={classes.box}>
                    <span>Price Min</span>
                    <input type='number' placeholder='Price Min' />
                </div>
                <div className={classes.box}>
                    <h4>Advance Filter</h4>
                </div>
                <button>
                    <i className='fa fa-search'></i>
                </button>
            </form>
            </div>
        </section>
    )
}

export default Filter;
